
const longestPalindrome = (str) => {
    const map = new Map()
    for (char of str) {
        if (map.has(char)) map.set(char, map.get(char) + 1)
        else map.set(char, 1)
    }

    let isOdd = false
    const result = [...map.values()].reduce((acc, prev) => {
        if (prev % 2 === 0) return acc + prev
        else {
            isOdd = true
            return acc + prev - 1
        }
    }, 0)

    return isOdd ? result + 1 : result
}

const str = ""
console.log(longestPalindrome(str))